-- author: Scrum Monkeys
-- desc:   The adventures and puzzles of a young Joe Martins
-- script: lua


--------------- CONSTANTS ----------------
btiles = {
		empty = 0,
  map_player = 34,
  goal = 33, 
  wall = 32,
  rock_min = 1,
  rock_max = 15
}

ftiles = {
	player = 256,
	pick = 258
}

screenw = 240
screenh = 136

----------------- MAIN --------------------

t=0
cur_lvl = 1

-- pick is a stack
lvls = {
	{ent = 0, pick = {5, 5, 5}}
}

p = {
	ent = 0,
	pick = {},
 x = 0,
 y = 0,
	dirx = 0,
 diry = 0
}

function TIC()
				
				if (t == 0) then
					load_level(cur_lvl)
				end
				
				input();
				update();
				render();
    
    t=t+1
end

function input()

	p.diry = 0
 p.dirx = 0

	if btnp(0) then p.diry=-1 end --up
 if btnp(1) then p.diry=1 end --down
 if btnp(2) then p.dirx=-1 end --left
 if btnp(3) then p.dirx=1 end --right 

end

------------------ UPDATE --------------

function update()

	newx = p.x + p.dirx
	newy = p.y + p.diry
	
	if newx ~= p.x or newy ~= p.y then
		
		tile = mget(newx, newy)
		
		if (
			handleRockTile(tile, newx, newy)
			or handleGoalTile(tile, newx, newy)
			or handleEmptyTile(tile, newx, newy)
		) then end
		
	end

end

function handleRockTile(tile, newx, newy)
	if tile >= btiles.rock_min and tile <= btiles.rock_max then
			if #p.pick > 0 then
				local cur_pick = p.pick[#p.pick]
				
				if cur_pick >= tile then
					p.pick[#p.pick] = nil
					mset(newx, newy, btiles.empty)
				end
			end
			
			return true
		end
		
		return false
end

function handleGoalTile()
	if tile == btiles.goal then
		p.x = newx
		p.y = newy
		
		mset(newx, newy, btiles.empty)
		
		-- do win
		
		return true
	end
			
	return false
end

function handleEmptyTile()

	if tile == btiles.empty then
		p.x = newx
		p.y = newy
		
		return true
	end
			
	return false
end

--------------- RENDERING ---------------------
 
function render()

 map(0, 0, 30, 17)
 spr(ftiles.player, p.x * 8, p.y * 8, 0)

	hud()

end

function hud()

	rect(0, 0, screenw, 8, 6)
	print("Level " .. tostring(cur_lvl), 2, 2)

	spr(ftiles.pick, 80, 0, 0)
	local	pick_values = "[ "
	for k, v in pairs(p.pick) do
		pick_values = pick_values .. tostring(v) .. " "
		if k == 1 then
			pick_values = pick_values .. "] "
		end
	end
	print(pick_values, 90, 2)

end


----------- LEVELS ----------------

function load_level(lvl)
	-- Load lvl properties
	local lvl_data = lvls[lvl]
	p.ent = lvl_data.ent
	p.pick = table.copy(lvl_data.pick)


	-- Copy map
	local mapx = 30 * (lvl % 8)
	local mapy = 17 * (lvl // 8)
	
	for i = mapx, mapx + 30 do
		for j = mapy, mapy + 17 do
			tile = mget(i, j)
			worldx = i % 30
			worldy = j % 17
			mset(worldx, worldy, tile)
			
			if tile == btiles.map_player then
				p.x = worldx
				p.y = worldy
				
				mset(worldx, worldy, btiles.empty)
			end
		end
	end

end











---------------- UTILS ---------------
function table.copy(t)
  local t2 = {}
  for k,v in pairs(t) do
    t2[k] = v
  end
  return t2
end
